import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {

}
<div class=" bg-dark mt-auto">
  <footer class="py-3 container">
    <ul class="nav justify-content-center border-bottom border-warning pb-3 mb-3">
      <li class="nav-item text-muted"><a routerLink="/home" routerLinkActive="active" class="nav-link px-2 text-reset">Home</a></li>
      <li class="nav-item text-muted"><a routerLink="/livros" routerLinkActive="active" class="nav-link px-2 text-reset">Livros</a></li>
      <li class="nav-item text-muted"><a routerLink="/sobre" routerLinkActive="active" class="nav-link px-2 text-reset">Sobre</a></li>
    </ul>
    <p class="text-center text-light">© 2025 Bruno, Isaias</p>
  </footer>
</div>
