import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="px-4 py-5 my-5 text-center">
  <i class="fa-solid fa-book mb-4" style="font-size: 4em;"></i>
  <h1 class="display-5 fw-bold">CRUD Livraria</h1>
  <div class="col-lg-6 mx-auto">
    <p class="lead mb-4">Projeto de Cadastro, Leitura, Edição e Exclusão de livros de uma Livraria. Front-End criado com Angular 15, Back-End criado com Java 8 + Spring Boot e o Banco de Dados H2 em memória. Criado durante o Treinamento Jovens Profissionais.</p>
    <div class="d-grid gap-2 d-sm-flex justify-content-sm-center">
      <button type="button" class="btn btn-warning btn-lg px-4 gap-3" routerLink="/livros">Livros</button>
      <button type="button" class="btn btn-outline-secondary btn-lg px-4" routerLink="/sobre">Sobre</button>
    </div>
  </div>
</div>
